# Lab 1. Kalishuk Ivan. Variant 1display("lab_1")function res = func(x,y)  res = (2 - y)*tan(x);
endfunction
function [res_x, res_y] = Euler(f, x, y, h)  res_x = [x];  res_y = [y];  for i=0:h:1    y = y + h*feval(f,x,y);    x = x + h;    res_x = [res_x, x];    res_y = [res_y, y];  endforendfunctionfunction [res_x, res_y] = Runge_Kutta(f, x, y, h, alfa)  res_x = [x];  res_y = [y];  for i = 0:h:1    res_f = feval(f,x,y);    y = y + h*((1 - alfa)*res_f + alfa*feval(f, x + h/(2*alfa), x + h/(2*alfa)*res_f));    x = x + h;    res_x = [res_x, x];    res_y = [res_y, y];  endforendfunctionfunction y = f(x)  y = 2 - 3 .*cos(x);endfunctiondisp("Main func")x0 = linspace(0,1,11)y0 = f(x0)disp("Euler")[x1,y1] = Euler('func',0,-1,0.01)disp("Modified Euler")[x2,y2] = Runge_Kutta('func',0,-1,0.01,1)disp("Heun")[x3,y3] = Runge_Kutta('func',0,-1,0.01,1/2)plot(x0,y0,'-r',x1,y1,'-b',x2,y2,'-y',x3,y3,'-g')legend("Main function", "Euler", "Modified Euler", "Heun")