# Lab 2. Kalishuk Ivan. Variant 1# Единственное я строил [a,b] = [1,10]tspan = [1 10];y0 = 0;[x4,y4] = ode23(@(x,y) 1/(2*e^y - x), tspan, y0);[x5,y5] = ode45(@(x,y) 1/(2*e^y - x), tspan, y0);function res = func(x,y)  res = 1/(2*e^y - x)endfunction  function [res_x, res_y] = Runge_Kutta_4(f, a, b, x, y, h)  res_x = [x];  res_y = [y];  for i = a:h:b    res_f = feval(f,x,y);    k1 = res_f;    k2 = feval(f,x+h/2,y + h*k1/2);    k3 = feval(f,x+h/2,y + h*k2/2);    k4 = feval(f,x+h,y + h*k3);    delta = h/6 * (k1 + 2*k2 + 2*k3 + k4);    x = x + h;    y = y + delta;    res_x = [res_x, x];    res_y = [res_y, y];  endforendfunctionfunction [res_x,res_y] = Kutta_Mersen(f,a,b,x,y,h, eps)  res_x = [x];  res_y = [y];  R = 1000;  for i = a:h:b    step = h;    while (R >= eps)      k1 = feval(f, x, y);      k2 = feval(f, x + step/3, y + 3 * k1 / 2);      k3 = feval(f, x + step/3, y + h * k1 / 6 + h * k2 / 6);      k4 = feval(f, x + h/2, y + h * k1 / 8 + 3 * h * k2 / 2);      k5 = feval(f, x + h, y + h * k1 / 2 - 3 * h * k2 / 2 + 2 * h * k4);      y_xvilka = y + 0.5 * h * (k1 - 3 * k3 + 4 * k4);      y = y + (step / 6) * (k1 + 4 * k3 + k5);      if (0.2*abs(y - y_xvilka) >= eps)        step = step/2;      else        break;      endif    endwhile    x = x+step;    res_y = [res_y,y];    res_x = [res_x,x];  endforendfunction        [res_x, res_y] = Runge_Kutta_4('func',1,10,1,0,0.01);[res_x_1, res_y_1] = Kutta_Mersen('func',1,10,1,0,0.01,0.0001);plot(res_x,res_y, x4, y4, x5, y5, res_x_1,res_y_1, res_x, log(res_x))legend('runge kut', "ode23","ode45", "kutta-mersen", "common log")